mapper.select:
SELECT COUNT(*) AS count FROM ( SELECT MAX(p.pay_no) AS max_pay_no, g.game_title, g.game_total, g.game_level, g.game_no, g.game_content, m.member_name, f.filegame_name, MAX(p.pay_date) AS pay_date, MAX(p.pay_enddate) AS pay_enddate, COALESCE(SUM(s.group_cnt), 0) AS total_group_cnt FROM pay p JOIN game g ON p.game_no = g.game_no LEFT JOIN studygroup s ON p.game_no = s.game_no AND p.member_id = s.member_id LEFT JOIN filegame f ON g.game_no = f.game_no JOIN member m ON p.member_id = m.member_id WHERE p.member_id = #{member_id}
AND g.game_title = #{game_title}
AND CURDATE() <= ( SELECT MAX(pay_enddate) FROM pay WHERE game_no = g.game_no )
GROUP BY g.game_no,g.game_title, g.game_total, g.game_level, g.game_content, m.member_name, f.filegame_name ORDER BY pay_enddate ASC ) AS subquery;

SELECT MAX(p.pay_no) AS pay_no, g.game_title, g.game_total, g.game_level, g.game_no, g.game_content, m.member_name, f.filegame_name, MAX(p.pay_date) AS pay_date, MAX(p.pay_enddate) AS pay_enddate, COALESCE(SUM(s.group_cnt), 0) AS group_cnt FROM pay p JOIN game g ON p.game_no = g.game_no LEFT JOIN studygroup s ON p.game_no = s.game_no AND p.member_id = s.member_id LEFT JOIN filegame f ON g.game_no = f.game_no JOIN member m ON p.member_id = m.member_id WHERE p.member_id = #{member_id}
AND g.game_title = #{game_title}
AND CURDATE() <= ( SELECT MAX(pay_enddate) FROM pay WHERE game_no = g.game_no ) GROUP BY g.game_no,g.game_title, g.game_total, g.game_level, g.game_content, m.member_name, f.filegame_name ORDER BY pay_enddate ASC LIMIT #{LIMIT} OFFSET #{OFFSET};

mapper.update:
UPDATE studygroup
SET group_name = #{group_name}, group_cnt = #{group_cnt},
    group_startdate = #{group_startdate}, group_enddate = #{group_enddate},
    group_intro = #{group_intro}
WHERE group_no = #{group_no};

