mapper.select:
SELECT m.member_name, gm.approve_date, gm.approve_state, g.game_no, g.game_title, g.game_total, g.game_level, g.game_no, sg.group_no, sg.group_name, sg.group_intro, sg.group_cnt, sg.group_nowcnt, sg.group_startdate, sg.group_enddate, fg.filegame_name FROM groupmember gm JOIN studygroup sg ON gm.group_no = sg.group_no JOIN member m ON sg.member_id = m.member_id JOIN game g ON gm.game_no = g.game_no LEFT JOIN filegame fg ON g.game_no = fg.game_no
WHERE gm.member_no = #{member_no}
AND m.member_name = #{member_name}
AND sg.group_name = #{group_name}
ORDER BY gm.approve_date DESC LIMIT #{LIMIT} OFFSET #{OFFSET};


SELECT COUNT(*)
FROM
    groupmember gm
JOIN studygroup sg ON gm.group_no = sg.group_no
JOIN member m ON sg.member_id = m.member_id
JOIN game g ON gm.game_no = g.game_no
LEFT JOIN filegame fg ON g.game_no = fg.game_no

WHERE gm.member_no = #{member_no}

    AND m.member_name = #{member_name}


    AND sg.group_name = #{group_name}


SELECT DISTINCT sg.group_name
FROM groupmember gm
JOIN studygroup sg ON gm.group_no = sg.group_no
WHERE gm.member_no = #{member_no}
ORDER BY sg.group_name ASC;


SELECT DISTINCT m.member_name
FROM groupmember gm
JOIN studygroup sg ON gm.group_no = sg.group_no
JOIN member m ON sg.member_id = m.member_id
WHERE gm.member_no = #{member_no}
ORDER BY m.member_name ASC;
